
import sqlite3
import time
import threading
import os
import sys
import winreg as reg
from plyer import notification
import pystray
from PIL import Image
import io
import base64
import win32event
import win32api
import win32con  # ¡También necesitas esto!
import ctypes


class AcuerdoMonitor:
    def __init__(self, db_path, check_interval=5):
        self.db_path = db_path
        self.check_interval = check_interval
        self.running = False
        self.last_checked_id = self._get_last_agreement_id()
        self.tray_icon = None
        self._setup_autostart()
        self._create_mutex()

    def _create_mutex(self):
        """Crea un mutex para evitar múltiples instancias"""
        self.mutex = win32event.CreateMutex(None, False, "Global\\AcuerdoMonitorMutex")
        if win32api.GetLastError() == 183:
            print("Ya hay una instancia en ejecución. Saliendo...")
            sys.exit(0)

    def _setup_autostart(self):
        """Agrega el programa al inicio de Windows"""
        try:
            key = reg.HKEY_CURRENT_USER
            key_path = r"Software\Microsoft\Windows\CurrentVersion\Run"

            with reg.OpenKey(key, key_path, 0, reg.KEY_ALL_ACCESS) as registry_key:
                try:
                    current_value = reg.QueryValueEx(registry_key, "AcuerdoMonitor")[0]
                    if current_value == sys.executable + ' "' + os.path.abspath(__file__) + '"':
                        return  # Ya está configurado correctamente
                except WindowsError:
                    pass

                reg.SetValueEx(
                    registry_key,
                    "AcuerdoMonitor",
                    0,
                    reg.REG_SZ,
                    sys.executable + ' "' + os.path.abspath(__file__) + '"'
                )
        except Exception as e:
            print(f"Error configurando autoinicio: {e}")

    def _get_last_agreement_id(self):
        """Obtiene el ID más reciente para comenzar el monitoreo"""
        try:
            conn = sqlite3.connect(self.db_path)
            cursor = conn.cursor()
            cursor.execute("SELECT MAX(id) FROM historial_acuerdos")
            last_id = cursor.fetchone()[0] or 0
            conn.close()
            return last_id
        except:
            return 0

    def _show_notification(self, title, message, pdf_path=None):
        """Muestra notificación con capacidad para abrir PDF"""
        try:
            notification.notify(
                title=title,
                message=message,
                app_name="Monitor de Acuerdos",
                timeout=10
            )

            if pdf_path and os.path.exists(pdf_path):
                # Abrir PDF automáticamente al detectar el cambio
                self._open_pdf(pdf_path)
        except Exception as e:
            print(f"Error mostrando notificación: {e}")

    def _open_pdf(self, pdf_path):
        """Abre el PDF con el visor predeterminado"""
        try:
            if os.path.exists(pdf_path):
                os.startfile(pdf_path)
        except Exception as e:
            print(f"Error abriendo PDF: {e}")

    def _check_closed_agreements(self):
        """Busca acuerdos recién cerrados"""
        try:
            conn = sqlite3.connect(self.db_path)
            cursor = conn.cursor()

            cursor.execute("""
                SELECT id, acuerdo, usuario_modifico, ruta_pdf 
                FROM historial_acuerdos 
                WHERE id > ? AND estatus = 'Cerrado'
                ORDER BY id DESC
            """, (self.last_checked_id,))

            new_closed = cursor.fetchall()

            for agreement in new_closed:
                id_, acuerdo, usuario, ruta_pdf = agreement
                self.last_checked_id = max(self.last_checked_id, id_)

                # Mostrar notificación
                self._show_notification(
                    "Acuerdo Cerrado",
                    f"El usuario {usuario} ha cerrado el acuerdo {acuerdo}",
                    ruta_pdf
                )

            conn.close()
            return len(new_closed)

        except Exception as e:
            print(f"Error verificando acuerdos: {e}")
            return 0

    def _monitor_changes(self):
        """Monitorea cambios en la base de datos"""
        while self.running:
            try:
                self._check_closed_agreements()
            except Exception as e:
                print(f"Error en monitor: {str(e)[:100]}...")

            time.sleep(self.check_interval)

    def _create_tray_icon(self):
        """Crea el icono en la bandeja del sistema"""
        # Icono base64 para evitar dependencias de archivos externos
        icon_data = base64.b64decode("""""")
        try:
            # Crear imagen para el icono desde datos en memoria
            image = Image.open(io.BytesIO(icon_data))

            # Configurar menú del icono
            menu = pystray.Menu(
                pystray.MenuItem("Salir", self.stop)
            )

            # Crear el icono en la bandeja
            self.tray_icon = pystray.Icon(
                "acuerdo_monitor",
                image,
                "Monitor de Acuerdos",
                menu
            )

            # Iniciar el icono en un hilo separado
            threading.Thread(
                target=self.tray_icon.run,
                daemon=True
            ).start()
        except Exception as e:
            print(f"Error creando icono en bandeja: {e}")

    def start(self):
        """Inicia el monitor en segundo plano"""
        if not self.running:
            self.running = True
            # Iniciar el icono en la bandeja
            self._create_tray_icon()

            # Iniciar el monitor de cambios
            monitor_thread = threading.Thread(
                target=self._monitor_changes,
                daemon=True
            )
            monitor_thread.start()

            print(f"Monitor iniciado. Observando acuerdos cerrados en: {self.db_path}")

    def stop(self):
        """Detiene el monitor limpiamente"""
        self.running = False
        if self.tray_icon:
            self.tray_icon.stop()
        if self.mutex:
            win32event.ReleaseMutex(self.mutex)
        sys.exit(0)

if __name__ == "__main__":
    DB_PATH = r"\\mercury\Mtto_Prod\00_Departamento_Mantenimiento\Minutas\minutas.db"

    # Ocultar la ventana de consola
    if sys.platform == "win32":
        import ctypes

        ctypes.windll.user32.ShowWindow(ctypes.windll.kernel32.GetConsoleWindow(), 0)

    monitor = AcuerdoMonitor(DB_PATH)
    monitor.start()

    try:
        # Mantener el programa en ejecución
        while True:
            time.sleep(1)
    except KeyboardInterrupt:
        monitor.stop()